import "./Carousel.css";
import $ from "jquery";
import "bootstrap/dist/css/bootstrap.css";
import Carousel from "react-bootstrap/Carousel";

const CarouselImages = () => {
  var slideIndex = 0;
  showSlides();
  var slides, dots;

  function showSlides() {
    var i;
    slides = document.getElementsByClassName("mySlides");
    dots = document.getElementsByClassName("dot");
    for (i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slideIndex++;
    if (slideIndex > slides.length) {
      slideIndex = 1;
    }
    slides[slideIndex - 1].style.display = "block";
    dots[slideIndex - 1].className += " active";
    setTimeout(showSlides, 2000); // Change image every 3 seconds
  }
  function currentSlide(index) {
    slideIndex = index;
    if (index > slides.length) {
      index = 1;
    } else if (index < 1) {
      index = slides.length;
    }
    for (let i = 0; i < slides.length; i++) {
      slides[i].style.display = "none";
    }
    for (let i = 0; i < dots.length; i++) {
      dots[i].className = dots[i].className.replace(" active", "");
    }
    slides[index - 1].style.display = "block";
    dots[index - 1].className += " active";
  }
  return (
    <section class="slider">
      <div id="slide">
        <div class="slideshow-container">
          <div class="mySlides fade">
            <img
              src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503577/H_M_iwwozr.png"
              class="slide-image"
              style="width: 100%"
            />
          </div>
          <div class="mySlides fade">
            <img
              src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503627/US_dihxc3.png"
              class="slide-image"
              style="width: 100%"
            />
          </div>
          <div class="mySlides fade">
            <img
              src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503648/w_odwc0j.png"
              class="slide-image"
              style="width: 100%"
            />
          </div>
          <div class="mySlides fade">
            <img
              src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503606/puma_jfiinm.png"
              class="slide-image"
              style="width: 100%"
            />
          </div>
        </div>
        <br />

        <div class="dots" style="text-align: center">
          <span class="dot" onclick="currentSlide(1)"></span>
          <span class="dot" onclick="currentSlide(2)"></span>
          <span class="dot" onclick="currentSlide(3)"></span>
          <span class="dot" onclick="currentSlide(4)"></span>
        </div>
      </div>
    </section>
    // <section className="slider">
    //   <div id="slide">
    //     <Carousel>
    //       <Carousel.Item interval={2000}>
    //         <img
    //           className="d-block w-100"
    //           src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503577/H_M_iwwozr.png"
    //           alt="First slide"
    //         />
    //       </Carousel.Item>
    //       <Carousel.Item interval={2000}>
    //         <img
    //           className="d-block w-100"
    //           src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503627/US_dihxc3.png"
    //           alt="Second slide"
    //         />
    //       </Carousel.Item>
    //       <Carousel.Item interval={2000}>
    //         <img
    //           className="d-block w-100"
    //           src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503648/w_odwc0j.png"
    //           alt="Third slide"
    //         />
    //       </Carousel.Item>
    //       <Carousel.Item interval={2000}>
    //         <img
    //           className="d-block w-100"
    //           src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1625503606/puma_jfiinm.png"
    //           alt="Third slide"
    //         />
    //       </Carousel.Item>
    //     </Carousel>
    //   </div>
    // </section>
  );
};

export default CarouselImages;
