import React, { useState } from "react";

const Input = () => {
  const [inputEntered, setEnteredInput] = useState("");

  const enteredInput = (event) => {
    setEnteredInput(event.target.value);
  };

  const submitHandler = (event) => {
    event.preventDefault();
    const data = {
      title: enteredInput,
    };
    console.log(data);
  };

  return (
    <React.Fragment>
      <div id="overlay">
        <img src="./img/preloader.gif" alt="Preloader icon" />
      </div>

      <main>
        <div id="table-section">
          <form action="/" onSubmit={submitHandler}>
            <img
              src="https://res.cloudinary.com/dt2q0lr2m/image/upload/v1626198759/search-icon_elwgui.svg"
              alt="Search Icon"
            />
            <input
              type="text"
              placeholder="Enter something"
              name="search-box"
              id="search-box"
              onChange={enteredInput}
            />
          </form>
        </div>
      </main>
    </React.Fragment>
  );
};
export default Input;
