import { useSelector } from "react-redux";

import { Link } from "react-router-dom";
import "./CheckOut.css";

const CheckOut = (props) => {
  const cartItems = useSelector((state) => state.items);

  console.log(props.data);
  return (
    <section className="checkout_page">
      <h1 className="checkout">Checkout</h1>
      <div class="cart_container">
        {cartItems.map((item) => {
          <div>
            <div className="left_page">
              <p>
                Total Items:{" "}
                <span className="no_of_items">{item.totalQuantity}</span>
              </p>
              <div className="cart_items">
                <div className="item">
                  <img src={item.preview} />

                  <div className="detail">
                    <h3>Name</h3>
                    <p>x{item.totalQuantity}</p>
                    <p>Amount: {item.totalPrice}</p>
                  </div>
                </div>
              </div>
            </div>
            <div className="right_page">
              <div className="total_amount">
                <h2>Total Amount</h2>
                <p>
                  Total Amount: Rs{" "}
                  <span className="total_price">{item.totalPrice}</span>
                </p>
                <Link to="/order">
                  <button className="place_order">Place Order</button>
                </Link>
              </div>
            </div>
          </div>;
        })}
      </div>
    </section>
  );
};

export default CheckOut;
