import { Fragment, useEffect } from "react";
import { useState } from "react";
import classes from "./Products.module.css";

const Products = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const response = fetch(
      "https://5d76bf96515d1a0014085cf9.mockapi.io/product"
    )
      .then((res) => res.json())
      .then((resp) => {
        setData(resp);
      });
  }, []);

  console.log(data);

  const productData = data.map((dat) =>
    !dat.isAccessory ? (
      <Fragment>
        <div className={classes.single_preview} key={dat.id}>
          <img
            className={classes.preview_img}
            src={dat.preview}
            alt="data_image"
          />
          <div className={classes.description}>
            <h3 className={classes.name}>{dat.name}</h3>
            <h4 className={classes.brand}>{dat.brand}</h4>
            <h3 className={classes.price}>Rs.{dat.price}</h3>
          </div>
        </div>
      </Fragment>
    ) : (
      <Fragment>
        <div className={classes.single_preview} key={dat.id}>
          <img
            className={classes.preview_img}
            src={dat.preview}
            alt="data_image"
          />
          <div className={classes.description}>
            <h3 className={classes.name}>{dat.name}</h3>
            <h4 className={classes.brand}>{dat.brand}</h4>
            <h3 className={classes.price}>Rs.{dat.price}</h3>
          </div>
        </div>
      </Fragment>
    )
  );

  const fff = <div>Loading</div>;

  return (
    <section className={classes.Products}>
      <div className={classes.clothing}>
        {/* <h2>Clothing</h2> */}
        {productData}
      </div>
      {fff}
    </section>
  );
};

export default Products;
