import { useEffect } from "react";
import { useState } from "react";
import classes from "./ProductDetail.module.css";

const ProductDetail = () => {
  const [data, setData] = useState({});
  const [images, setImages] = useState([]);

  var productId = window.location.search.split("=")[1];

  ImageHandler = () => {};

  useEffect(() => {
    const response = fetch(
      `https://5d76bf96515d1a0014085cf9.mockapi.io/product/` + productId
    )
      .then((res) => res.json())
      .then((resp) => {
        setData(resp);
        setImages(resp.photos);
        return resp;
      });
  }, []);
  console.log(data);
  console.log(images);

  const left_section = (
    <img
      src={data.preview}
      className={classes.main_image}
      alt="Product_image"
    />
  );

  const right_section = (
    <div className={classes.product_description}>
      <h1 className={classes.name}>{data.name}</h1>
      <h4 className={classes.brand}>{data.brand}</h4>
      <h3 className={classes.price}>Price: Rs{data.price}</h3>
      <div className={classes.description}>
        <h3>Description</h3>
        <p className={classes.desc}>{data.description}</p>
      </div>
      <div className={classes.product_preview}>
        <h3>Product Preview</h3>
        <div className={classes.preview_img}>
          {images.map((dat) => (
            <img
              key={dat.id}
              src={dat}
              className={classes.preImg}
              alt="Preview_Images"
              onClick={ImageHandler}
            />
          ))}
        </div>
      </div>
    </div>
  );

  return (
    <section className={classes.product_details}>
      <div className={classes.left_section}>{left_section}</div>
      <div className={classes.right_section}>{right_section}</div>
    </section>
  );
};

export default ProductDetail;
