import classes from "./Navigation.module.css";
import { useContext } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import { faSearch } from "@fortawesome/free-solid-svg-icons";
import { faShoppingCart } from "@fortawesome/free-solid-svg-icons";
import { faUserCircle } from "@fortawesome/free-solid-svg-icons";
import { Link, NavLink } from "react-router-dom";
import CartProvider from "../store/cartProvider";
import CartContext from "../store/cartContext";

const Navigation = () => {
  const cartCtx = useContext(CartContext);

  const numberOfCartItems = cartCtx.items.length;

  // const numberOfCartItems = cartCtx.items.reduce((currentNumber, item) => {
  //   return currentNumber + item.price;
  // }, 0);

  return (
    <CartProvider>
      <div className={classes.top_bar}>
        <div>
          <Link to="/home" className={classes.navlink}>
            <h4 className={classes.logo}>
              <span className={classes.logo_color}>SHOP</span>LANE
            </h4>
          </Link>
        </div>
        <div className={classes.center_nav}>
          <div className={classes.home}>
            <NavLink
              to="/home"
              activeClassName={classes.active}
              className={classes.navlink}
            >
              HOME
            </NavLink>
          </div>
          <div className={classes.clothing_nav}>
            <NavLink
              to="/home/clothing"
              activeClassName={classes.active}
              className={classes.navlink}
            >
              CLOTHING
            </NavLink>
          </div>
          <div className={classes.accessoires}>
            <NavLink
              to="/home/accessories"
              activeClassName={classes.active}
              className={classes.navlink}
            >
              ACCESSORIES
            </NavLink>
          </div>
        </div>
        <div className={classes.right_nav}>
          <div className={classes.icons}>
            <FontAwesomeIcon icon={faSearch} />
          </div>
          <div className={classes.icons}>
            <Link to="/checkout" className={classes.link}>
              <FontAwesomeIcon icon={faShoppingCart} />
              <div className={classes.cart_count}>
                <p>{numberOfCartItems}</p>
              </div>
            </Link>
          </div>
          <div className={classes.icons}>
            <FontAwesomeIcon icon={faUserCircle} />
          </div>
        </div>
      </div>
    </CartProvider>
  );
};

export default Navigation;
