const CartButton = () => {
  const CartContext = React.createContext({
    items: [],
    totalAmount: 0,
    addItem: (item) => {},
    removeItem: (id) => {},
  });
  const cartCtx = useContext(CartContext);
  const addToCartHandler = () => {
    cartCtx.addItem({
      id: props.id,
      name: props.name,
      amount: amount,
      price: props.price,
    });
  };
  return (
    <div class="button">
      <button class="add_to_cart" onClick={addToCartHandler}>
        Add to Cart
      </button>
    </div>
  );
};

export default CartButton;
